import classes from "./Email.module.css";
import { useRef } from "react";
import { Link } from "react-router-dom";
import InputContext from "./store/input-context";
import { useContext } from "react";

function FirstName() {
  const inputCtx = useContext(InputContext);
  const nameInputRef = useRef();

  function firstNameHandler() {
    inputCtx.userInput(nameInputRef.current.value);
  }

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">firstname</label>
          <input type="text" id="email" required ref={nameInputRef} />
        </div>
      </form>
      <div className={classes.actions}>
        <Link to="/email" className={classes.button}>
          Previous
        </Link>

        <Link to="/lastname" className={classes.button} onClick={emailHandler}>
          Next
        </Link>
      </div>
    </div>
  );
}

export default FirstName;
