import classes from "./Email.module.css";
import { useHistory } from "react-router-dom";

function ProgressBar() {
  const history = useHistory();
  console.log(history);

  let progressValue = 0;
  if (history.location.pathname === "/email") {
    progressValue = 30;
  }

  if (history.location.pathname === "/firstname") {
    progressValue = 60;
  }

  if (history.location.pathname === "/lastname") {
    progressValue = 90;
  }

  return (
    <section>
      <div className={classes.progressSection}>
        <progress value={progressValue} max="100">
          {" "}
          52%{" "}
        </progress>
        <ul className={classes.progressCircle}>
          <li>
            <div
              className={
                classes[
                  history.location.pathname === "/email" ? "insideCircle" : ""
                ]
              }
            ></div>
          </li>
          <li>
            <div
              className={
                classes[
                  history.location.pathname === "/firstname"
                    ? "insideCircle"
                    : ""
                ]
              }
            ></div>
          </li>
          <li>
            <div
              className={
                classes[
                  history.location.pathname === "/lastame" ? "insideCircle" : ""
                ]
              }
            ></div>
          </li>
          <li>
            <div
              className={
                classes[
                  history.location.pathname === "/gender" ? "insideCircle" : ""
                ]
              }
            ></div>
          </li>
        </ul>
      </div>
    </section>
  );
}

export default ProgressBar;
