import classes from "./Email.module.css";
import { useRef } from "react";
import { Link, useHistory } from "react-router-dom";
import InputContext from "./store/input-context";
import { useContext } from "react";

function LastName() {
  const lastNameInputRef = useRef();
  const inputCtx = useContext(InputContext);
  const history = useHistory();

  function lastNameHandler() {
    history.push("/gender");
    inputCtx.userInput(lastNameInputRef.current.value);
    inputCtx.genderInsideCircleFn(true);
  }

  function lastNameprogressHandler() {
    inputCtx.progress(60);
    inputCtx.lastNameInsideCircleFn(false);
  }

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">Lastname</label>
          <input type="text" id="email" required ref={lastNameInputRef} />
        </div>
      </form>
      <div className={classes.actions}>
        <Link
          to="/firstName"
          className={classes.button}
          onClick={lastNameprogressHandler}
        >
          Previous
        </Link>

        <button className={classes.button} onClick={lastNameHandler}>
          Next
        </button>
      </div>
    </div>
  );
}

export default LastName;
