import classes from "./Email.module.css";
import { useRef } from "react";
import { Link } from "react-router-dom";
import InputContext from "./store/input-context";
import { useContext } from "react";

function FirstName() {
  const inputCtx = useContext(InputContext);
  const firstNameInputRef = useRef();

  function firstNameHandler() {
    inputCtx.userInput(firstNameInputRef.current.value);
    inputCtx.progress(90);
  }

  function firstNameprogressHandler() {
    inputCtx.progress(30);
    inputCtx.lasttNameInsideCircleFn(true);
  }

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">firstname</label>
          <input type="text" id="email" required ref={firstNameInputRef} />
        </div>
      </form>
      <div className={classes.actions}>
        <Link
          to="/email"
          className={classes.button}
          onClick={firstNameprogressHandler}
        >
          Previous
        </Link>

        <Link
          to="/lastname"
          className={classes.button}
          onClick={firstNameHandler}
        >
          Next
        </Link>
      </div>
    </div>
  );
}

export default FirstName;
