import classes from "./Email.module.css";
import { useContext, useState } from "react";
import { useRef } from "react";
import { Link, useHistory } from "react-router-dom";
import InputContext from "./store/input-context";

function Email() {
  const content = <p>Please enter some inputs</p>;
  const inputCtx = useContext(InputContext);
  const history = useHistory();

  const [helper, setHelper] = useState(false);

  const emailInputRef = useRef();

  let [enteredValue, setEnteredValue] = useState("");

  function emailHandler(event) {
    event.preventDefault();
    console.log(emailInputRef.current.value);
    if (emailInputRef.current.value !== "") {
      setHelper(false);
      history.push("/firstname");
      //inputCtx.userInput(emailInputRef.current.value);
      enteredValue = localStorage.setItem("email", emailInputRef.current.value);
      inputCtx.progress(60);
      inputCtx.firstNameInsideCircleFn(true);
    } else {
      setHelper(true);
    }
  }

  console.log(inputCtx.userInputArray);

  return (
    <div>
      <form onChange={emailHandler}>
        <div className={classes.control}>
          <label htmlFor="email">Your Email</label>
          <input
            type="email"
            id="email"
            required
            value={enteredValue}
            ref={emailInputRef}
          />
          {helper ? content : ""}
        </div>
      </form>
      <div className={classes.actions}>
        <button className={classes.button} onClick={emailHandler}>
          Next
        </button>
      </div>
    </div>
  );
}

export default Email;
