import classes from "./Email.module.css";
import { useHistory } from "react-router-dom";
import { useEffect, useState } from "react";
import InputContext from "./store/input-context";
import { useContext } from "react";

function ProgressBar() {
  const history = useHistory();
  console.log(history);

  const inputCtx = useContext(InputContext);

  // let [progressValue, setProgressValue] = useState(30);

  // useEffect(() => {
  //   if ((history.location.pathname = "/email")) {
  //     setProgressValue = 30;
  //   } else if ((history.location.pathname = "/firstname")) {
  //     setProgressValue = 60;
  //   } else if ((history.location.pathname = "/lastname")) {
  //     setProgressValue = 90;
  //   }
  // }, [history.location.pathname]);

  console.log(inputCtx.progressValue);

  return (
    <section>
      <div className={classes.progressSection}>
        <progress value={inputCtx.progressValue} max="100"></progress>
        <ul className={classes.progressCircle}>
          <li>
            <div className={classes.insideCircle}></div>
          </li>

          <li>
            <div
              className={
                classes[inputCtx.firstNameInsideCircle ? "insideCircle" : ""]
              }
            ></div>
          </li>
          <li>
            <div></div>
          </li>
          <li>
            <div
              className={
                classes[
                  history.location.pathname === "/gender" ? "insideCircle" : ""
                ]
              }
            ></div>
          </li>
        </ul>
      </div>
    </section>
  );
}

export default ProgressBar;
