import classes from "./Email.module.css";
import { useRef } from "react";
import { Link, useHistory } from "react-router-dom";
import InputContext from "./store/input-context";
import { useContext, useState } from "react";

function Gender() {
  const content = <p>Please enter some inputs</p>;
  const genderInputRef = useRef();
  const inputCtx = useContext(InputContext);
  const history = useHistory();
  const [helper, setHelper] = useState(false);

  function genderHandler() {
    inputCtx.userInput(genderInputRef.current.value);
    console.log(inputCtx.userInputArray);

    const [email, firstname, lastname, gender] = inputCtx.userInputArray;
    console.log(email, firstname, lastname, gender);
    alert(
      `Email : ${email}\n Firstname : ${firstname}\n Lastname : ${lastname}\n Gender : ${gender}`
    );
  }

  function previousHandler() {
    history.push("/lastname");
    inputCtx.genderInsideCircleFn(false);
  }

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">Gender</label>
          <input type="text" id="email" required ref={genderInputRef} />
          {helper ? content : ""}
        </div>
      </form>
      <div className={classes.actions}>
        <Link
          to="/lastName"
          className={classes.button}
          onClick={previousHandler}
        >
          Previous
        </Link>

        <button className={classes.button} onClick={genderHandler}>
          Submit
        </button>
      </div>
    </div>
  );
}

export default Gender;
