import React, { useState, useEffect, useCallback } from "react";

const InputContext = React.createContext({
  userInput: {},
});

export const InputContextProvider = (props) => {
  // const [userInputArray, setuserInputArray] = useState([]);

  let userInputArray = [];

  let [progressValue, setProgressValue] = useState(30);

  const userInput = function (userValue) {
    userInputArray.push(userValue);
    // setuserInputArray((prevState) => {
    //   // prevState.concat(userInputArray);
    //   console.log(prevState);
    // });
  };

  const contextValue = {
    userInput: userInput,
    userInputArray: userInputArray,
    progressValue: progressValue,
  };

  return (
    <InputContext.Provider value={contextValue}>
      {props.children}
    </InputContext.Provider>
  );
};

export default InputContext;
