import classes from "./Email.module.css";
import { useRef } from "react";
import { Link } from "react-router-dom";
import InputContext from "./store/input-context";
import { useContext } from "react";

function Gender() {
  const genderInputRef = useRef();
  const inputCtx = useContext(InputContext);

  function genderHandler() {
    let userObj = {
      email: "",
      firstName: "",
      lastName: "",
      gender: "",
    };

    inputCtx.userInput(genderInputRef.current.value);
    console.log(inputCtx.userInputArray);

    for (const keys of userObj) {
      let i = 0;
      if (i < inputCtx.userInputArray.length) {
        userObj[keys] = inputCtx.userInputArray[i];
        i++;
      }
    }

    console.log(userObj);
  }

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">Gender</label>
          <input type="text" id="email" required ref={genderInputRef} />
        </div>
      </form>
      <div className={classes.actions}>
        <Link to="/lastName" className={classes.button}>
          Previous
        </Link>

        <Link
          to="/FirstName"
          className={classes.button}
          onClick={genderHandler}
        >
          Submit
        </Link>
      </div>
    </div>
  );
}

export default Gender;
