import classes from "./Email.module.css";
import { useContext } from "react";
import { useRef } from "react";
import { Link } from "react-router-dom";
import InputContext from "./store/input-context";

function Email() {
  const inputCtx = useContext(InputContext);

  const emailInputRef = useRef();
  // const enteredEmail = emailInputRef.current.value;
  // console.log(enteredEmail);
  function emailHandler() {
    console.log(emailInputRef.current.value);

    inputCtx.userInput(emailInputRef.current.value);
    inputCtx.progress(60);
    inputCtx.firstNameInsideCircleFn(true);
  }

  console.log(inputCtx.userInput);

  return (
    <div>
      <form>
        <div className={classes.control}>
          <label htmlFor="email">Your Email</label>
          <input type="email" id="email" required ref={emailInputRef} />
          <p>We will never share your email</p>
        </div>
      </form>
      <div className={classes.actions}>
        <Link to="/firstname" className={classes.button} onClick={emailHandler}>
          Next
        </Link>
      </div>
    </div>
  );
}

export default Email;
