import { useEffect, useState } from "react";
import { Chart } from "react-google-charts";

function PieChart() {
  const [data1, setData] = useState("");

  // const getData = await fetch(
  //   "http://216.48.189.38:9090/api/v1/query?query=container_cpu_usage_seconds_total{namespace=%22sathiyapk%22,container=%22POD%22}"
  // );
  // const jsnData = getData.json();
  // const realData = await jsnData;
  // setData(realData);

  const getData = fetch(
    "http://216.48.189.38:9090/api/v1/query?query=container_cpu_usage_seconds_total{namespace=%22sathiyapk%22,container=%22POD%22}"
  )
    .then((getData) => getData.json())
    .then((realData) => setData(realData));
  setData(realData);

  console.log(data1);

  const destructuredData = data1.data;

  console.log(destructuredData);

  let datafromUrl = [["Task", "Hours per Day"]];

  // for (const dataInArray of destructuredData) {
  //   let metricValue = [];
  //   metricValue.push(dataInArray.metric.pod, dataInArray.value[0]);
  //   datafromUrl.push(metricValue);
  // }

  const data = [
    ["Task", "Hours per Day"],
    ["Work", 11],
    ["Eat", 2],
    ["Commute", 2],
    ["Watch TV", 2],
    ["Sleep", 7],
  ];

  const options = {
    title: "My Daily Activities",
  };

  return (
    <Chart
      chartType="PieChart"
      data={data}
      options={options}
      width={"100%"}
      height={"400px"}
    />
  );
}

export default PieChart;
